@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --radius: 0.75rem;
  --font-sans: 'Space Grotesk', system-ui, sans-serif;

  /* Deep teal/cyan education theme */
  --background: oklch(0.985 0.005 200);
  --foreground: oklch(0.18 0.02 240);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 240);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 240);
  --primary: oklch(0.52 0.14 195);
  --primary-foreground: oklch(1 0 0);
  --secondary: oklch(0.96 0.01 200);
  --secondary-foreground: oklch(0.35 0.06 195);
  --muted: oklch(0.96 0.008 200);
  --muted-foreground: oklch(0.5 0.02 240);
  --accent: oklch(0.92 0.03 195);
  --accent-foreground: oklch(0.35 0.08 195);
  --destructive: oklch(0.58 0.22 25);
  --border: oklch(0.91 0.015 200);
  --input: oklch(0.91 0.015 200);
  --ring: oklch(0.52 0.14 195);

  /* Chart colors - harmonious with teal */
  --chart-1: oklch(0.52 0.14 195);
  --chart-2: oklch(0.65 0.15 160);
  --chart-3: oklch(0.7 0.12 85);
  --chart-4: oklch(0.6 0.18 280);
  --chart-5: oklch(0.55 0.2 30);

  /* Sidebar - elegant dark teal */
  --sidebar: oklch(0.22 0.04 220);
  --sidebar-foreground: oklch(0.92 0.01 200);
  --sidebar-primary: oklch(0.65 0.15 180);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.28 0.04 220);
  --sidebar-accent-foreground: oklch(0.92 0.01 200);
  --sidebar-border: oklch(0.32 0.03 220);
  --sidebar-ring: oklch(0.52 0.14 195);

  /* Custom design tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.52 0.14 195), oklch(0.45 0.12 220));
  --gradient-card: linear-gradient(160deg, oklch(1 0 0), oklch(0.97 0.01 200));
  --gradient-success: linear-gradient(135deg, oklch(0.65 0.18 155), oklch(0.55 0.15 170));
  --gradient-warning: linear-gradient(135deg, oklch(0.75 0.16 75), oklch(0.65 0.18 55));
  --gradient-info: linear-gradient(135deg, oklch(0.6 0.12 250), oklch(0.5 0.14 270));
  --shadow-card: 0 4px 20px -4px oklch(0.52 0.14 195 / 0.12);
  --shadow-card-hover: 0 8px 30px -4px oklch(0.52 0.14 195 / 0.2);
  --shadow-hero: 0 20px 50px -12px oklch(0.52 0.14 195 / 0.35);
  --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.205 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.205 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.922 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(1 0 0 / 10%);
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
